async function handleUpload(stories) {
  for (const story_ind in stories) {
    stories[story_ind].title = "" + story_ind;
  }

  const dataset = [];

  for (const story of stories) {
    let headlinesSet = new Set();
    let slugsSet = new Set();
    const storyTitle = story.title;
    const news = story.news;
    for (const n of news) {
      let body = n.body;
      let headline = n.headline || n.title;
      if (headline) {
        if (
          headline.match(/^[^\s\+]+\+[^\s]+\s/) ||
          headline.indexOf("(версия") !== -1 ||
          headlinesSet.has(headline) ||
          slugsSet.has(n.slug)
        )
          continue;
        headline = headline.replace("ВЗГЛЯД: ", "");
        headlinesSet.add(headline);
        if (n.slugline && n.slugline != "МОЛНИЯ") {
          slugsSet.add(n.slugline);
        }
      }

      if (n.codes && n.codes.indexOf("920020000000000000") != -1) {
        body = headline;
      }

      let X;
      if (headline) {
        X = headline + ". " + body;
      } else {
        X = body;
      }

      dataset.push({ X, y: storyTitle });
    }
  }

  const response = await fetch("/get_preds", {
    method: "POST", // *GET, POST, PUT, DELETE, etc.
    mode: "cors", // no-cors, *cors, same-origin
    cache: "no-cache", // *default, no-cache, reload, force-cache, only-if-cached
    credentials: "same-origin", // include, *same-origin, omit
    redirect: "follow", // manual, *follow, error
    referrerPolicy: "no-referrer", // no-referrer, *client
    headers: {
      "Content-Type": "application/json",
      // 'Content-Type': 'application/x-www-form-urlencoded',
    },
    body: JSON.stringify(dataset), // body data type must match "Content-Type" header
  }).then((res) => res.json());

  const { story_id, story_name } = response;
  for (const i in story_id) {
    for (const story of stories) {
      if (story.title == story_id[i]) {
        story.title = story_name[i];
      }
    }
  }

  for (const story of stories) {
    console.log(story.title);
  }
}

function onChange(event) {
  var reader = new FileReader();
  reader.onload = onReaderLoad;
  reader.readAsText(event.target.files[0]);
}

function onReaderLoad(event) {
  handleUpload(JSON.parse(event.target.result));
}

document
  .querySelector("input[type='file']")
  .addEventListener("change", onChange);

function handleExample() {
  handleUpload(exampleInput);
}

document.querySelector(".example").addEventListener("click", handleExample);
